function timing(){seconds++,seconds>60&&minutes++,minutes>60&&hours++,updateTimer()}function timer(){interval=setInterval(timing,1e3)}function updateTimer(){timerOutput.innerHTML=hours+" : "+minutes+" : "+seconds}var timerOutput=document.querySelector(".js-time-output"),playBtn=document.querySelector(".js-play"),pauseBtn=document.querySelector(".js-pause"),stopBtn=document.querySelector(".js-stop"),time=new Date,seconds=0,minutes=0,hours=0,interval;playBtn.addEventListener("click",timer),pauseBtn.addEventListener("click",function(){clearInterval(interval)}),stopBtn.addEventListener("click",function(){hours=0,minutes=0,seconds=0,clearInterval(interval),updateTimer()});